mapper.insert:
INSERT INTO users ( users_seq, users_code, users_name, users_password, users_email, users_full_name, department_code, unit_code, users_created_at, users_updated_at, users_is_admin ) VALUES ( users_seq.NEXTVAL, #{users_code}, #{users_name}, #{users_password}, #{users_email}, #{users_full_name}, #{department_code}, #{unit_code}, SYSTIMESTAMP, SYSTIMESTAMP,
#{users_is_admin}
0
)

mapper.update:
UPDATE users SET users_name = #{users_name}, users_password = #{users_password}, users_email = #{users_email}, users_full_name = #{users_full_name}, department_code = #{department_code}, unit_code = #{unit_code}, users_created_at = #{users_created_at}, users_updated_at = SYSTIMESTAMP, users_is_admin =
#{users_is_admin}
0
WHERE users_seq = #{users_seq}
